import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//保存しようとしている項目が現在のリストに存在するときtrueを返す
function samelist(a,...array){
  for (let i = 0; i < array.length; i++) {
    if(array[i]===a){
      return true;
    }
  }
}

class Table extends React.Component{
  constructor(props){
    super(props);
    this.state={
      lists: [],
      typinglist: '',
      edit: false,
      editnumber: 0,
      donelists: [],
      hyouji: false,
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.delete = this.delete.bind(this);
    this.handleClick = this.handleClick.bind(this)
  }

  handleSubmit(event){
    const newlist=this.state.typinglist;
    const lists=this.state.lists;
    if (newlist){
      if (this.state.edit) {
        if (lists[this.state.editnumber]===newlist) {
          alert('編集項目が変更されていません');
        }else if(samelist(newlist,...lists)){
          alert('同名の項目が存在します');
        }else{
          const removes=lists.splice(this.state.editnumber,1,newlist);
          this.setState({
            lists: lists,
            typinglist: '',
            edit: false,
            editnumber: 0,
          })
        }
      }else{
        if(samelist(newlist,...lists)){
          alert('同名の項目が存在します');
        }else{
          this.setState({
            lists: lists.concat([newlist]),
            typinglist: '',
            edit: false,
            editnumber: 0,
          });
        }
      }
    }else{
      alert('やることを入力してください');
    }
  }

  handleChange(event){
      this.setState({
        typinglist: event.target.value,
      })
  }

  handleClick(list,number){
    const lists=this.state.lists;
    this.setState({
      typinglist: list,
      edit: true,
      editnumber: number,
    })
  }

  delete(i){
    let lists=this.state.lists;
    const removes=lists.splice(i,1);
    this.setState({
      lists: lists
    })
  }

  hyoujiClick(){
    this.setState({
      hyouji: !this.state.hyouji,
    })
  }

  doneClick(list,number){
    let lists=this.state.lists;
    const removes=lists.splice(i,1);
    let donelists=this.state.donelists;
    this.setState({
      lists: lists,
      doneLists: donelists
    })
  }

  render(){
    return(
      <div className="table">
        <h1>Todoリスト作成</h1>
          <div className="form">
            <Form
              onSubmit={this.handleSubmit}
              onChange={this.handleChange}
              newlist={this.state.typinglist}
            />
          </div>
          <div className="list">
            <List
              lists={this.state.lists}
              onClick={(list,number)=>this.handleClick(list,number)}
              delete={(number)=>this.delete(number)}
              doneClick={(list,number)=>doneClick(list,number)}
            />
          </div>
          <div>
            <Done
              isDonelist={!!this.state.donelists.legth}
              hyouji={this.state.hyouji}
              onClick={()=>this.hyoujiClick()}
              donelists={this.state.donelists}
            />
          </div>
      </div>
    );
  }
}

class Done extends React.Component{
  render(){
    let hyouji= this.props.hyouji? '表示しているよ':'表示ボタン';
    return(
      <div>
        <button onClick={()=>this.props.onClick}>
          '表示ボタン'
        </button>
        <DoneLists
          donelists={this.props.donelists}
        />
      </div>
    )
  }
}

class DoneLists extends React.Component{
  render(){
    const lists=this.props.donelists.map((list,number)=>{
      return(
        <div className='list'>
          <li key={number}>
              {list}
          </li>
        </div>
        )
    });
    return lists;
  }
}

class List extends React.Component{
  render(){
    const lists=this.props.lists.map((list,number)=>{
      return(
        <div className='list'>
          <li key={number}>
            <button onClick={()=>this.props.onClick(list,number)}>
              {list}
            </button>
            <button onClick={()=>this.props.doneClick(list,number)}>
              完了
            </button>
            <Delete
              delete={()=>this.props.delete(number)}
            />
          </li>
        </div>
        )
    });
    return lists;
  }
}

class Delete extends React.Component{
  render(){
    return(
      <button className='delete' onClick={()=>this.props.delete()}>削除</button>
    )
  }
}

class Form extends React.Component{
  render(){
    return(
        <form>
            <input className='box' type="text"
              value={this.props.newlist} onChange={this.props.onChange}
            />
            <input className='btn' type="button" value='保存' onClick={this.props.onSubmit}/>
        </form>
    );
  }
}

ReactDOM.render(
    <Table />,
  document.getElementById('root')
);
