import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Table extends React.Component{
  constructor(props){
    super(props);
    this.state={
      lists: ['タスク１', 'タスク２'],
      typinglist: ''
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.delete = this.delete.bind(this);
  }

  handleSubmit(event){
    const lists=this.state.lists;
    this.setState({
      lists: lists.concat([this.state.typinglist]),
      typinglist: ''
    });
  }

  handleChange(event){
      this.setState({
        typinglist: event.target.value
      })
  }

  delete(i){
    const lists=this.state.lists;
    this.setState({
      lists: lists.splice(i,1)
    })
  }

  render(){
    const lists=this.state.lists.map((list,number)=>{
      return(
          <li key={number}>
            {list}
            <Delete
              delete={()=>this.delete(number)}
            />
          </li>
        )
    });

    return(
      <div className="table">
        <h1>Todoリスト</h1>
          <div className="list">
            {lists}
          </div>
          <div className="form">
            <Form
              onSubmit={this.handleSubmit}
              onChange={this.handleChange}
              newlist={this.state.typinglist}
            />
          </div>
      </div>
    );
  }
}

class Delete extends React.Component{
  render(){
    return(
      <button onClick={()=>this.props.delete}>'削除'</button>
    )
  }
}

class Form extends React.Component{
  render(){
    return(
        <form>
          <label>
            Todo:
            <input className='box' type="text"
              value={this.props.newlist} onChange={this.props.onChange}
            />
          </label>
          <input className='btn' type="button" value='追加' onClick={this.props.onSubmit}/>
        </form>
    );
  }
}

ReactDOM.render(
    <Table />,
  document.getElementById('root')
);
