import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Table extends React.Component{
  constructor(props){
    super(props);
    this.state={
      lists: ['タスク１'],
    }
  }

  handleChange(event){
    let lists=this.state.lists;
    this.setState({
      lists: lists.concat([event.target.value])
    });
  }

  render(){
    const length=this.state.lists.legth;
    const lists=this.state.lists.map((list,number)=>{
      return(
          <li key={number}>
            {list}
          </li>
        )
    });

    return(
      <div className="table">
        <h1>Todoリスト</h1>
          <div className="list">
            {lists}
          </div>
          <div className="form">
            <Form
              newlist={this.state.lists[length-1]}
              onChange={()=>this.handleChange}
            />
          </div>
      </div>
    );
  }
}

class Form extends React.Component{
  render(){
    return(
      <form>
        <label>
          <input type="text"
          value={this.props.newlist} onChange={()=>this.props.onChange}
          />
        </label>
        <input type="submit" value="追加" />
      </form>
    );
  }
}

ReactDOM.render(
    <Table />,
  document.getElementById('root')
);
